import React, { Component } from 'react'
import Item from '../Item'
import list from './index.module.css'

export default class List extends Component {

  moveMouse = (flag)=>{
  
    state = {
      mouse
    }

    return ()=>{
      console.log(flag)
    }
  }

  render() {
    return (
      <div style={{display:'flex',justifyContent:'center',borderRadius:'1px dotted lightgrey',marginTop:'10px'}}>
        <ul className={list.list}>
          {this.props.todoList.map(item=>{
            return <li onMouseLeave={this.moveMouse(false)} onMouseEnter={this.moveMouse(true)} key={item.id} className={list.listLi}><Item {...item} deleteTodoList={this.props.deleteTodoList}></Item></li>
          })}
        </ul>
      </div>
    )
  }
}
