import { nanoid } from 'nanoid';
import { Component } from 'react';
import './App.css';
import Footer from './components/Footer';
import Header from './components/Header';
import List from './components/List';

class App extends Component {

  state = {
    todoList: [
      { id: 1, event: '吃饭', done: true },
      { id: 2, event: '睡觉', done: true },
      { id: 3, event: '打代码', done: false }
    ]
  }

  addTodoList = (addValue) => {
    const valueObject = { id: nanoid(), event: addValue, done: false }
    this.setState({ todoList: [...this.state.todoList, valueObject] })
  }

  deleteTodoList = (eventId) => {
    const newTodoList = this.state.todoList.filter(item => item.id !== eventId)
    this.setState({ todoList: newTodoList })
  }

  doneValue = (doneValue,id)=>{
    const newTodoList = this.state.todoList.map((item, index) => {
      if (id === index) {
        return { ...item, done: doneValue };
      }
      return item;
    });

    this.setState({ todoList: newTodoList })
    console.log('@',this.state.todoList)
  }


  render() {
    return (
      <div className='app' >
        <Header addTodoList={this.addTodoList}></Header>
        <List doneValue={this.doneValue} todoList={this.state.todoList} deleteTodoList={this.deleteTodoList}></List>
        <Footer></Footer>
      </div>
    );
  }
}

export default App;
