import React, { Component } from 'react'
import './index.module.css'

export default class Item extends Component {

  deleteTodoList = ()=>{
    this.props.deleteTodoList(this.props.id)
  }

  doneValue = (event,id)=>{
    return ()=>{
      this.props.doneValue(event.target.checked,id)
    }
  }

  render() {
    return (
      <div style={{display:'flex',justifyContent:'space-between'}}>
        <label>
          <input type="checkbox" defaultChecked={this.props.done} onChange={this.doneValue(e.ta,this.props.id)}/>
          <span>{this.props.event}</span>
        </label>
        <button style={{display:this.props.showBtn.id === this.props.id && this.props.showBtn.done ? "block" : "none"}} onClick={this.deleteTodoList}>删除</button>
      </div>
    )
  }
}
