import React, { Component } from 'react'
import Item from '../Item'
import list from './index.module.css'

export default class List extends Component {

  state = {
    mouse: {done:false,id:null}
  }

  moveMouse = (flag,id) => {
    return () => {
      this.setState({ mouse: {done:flag,id:id} })
    }
  }

  render() {
    return (
      <div style={{ display: 'flex', justifyContent: 'center', borderRadius: '1px dotted lightgrey', marginTop: '10px' }}>
        <ul className={list.list}>
          {this.props.todoList.map(item => {
            return <li style={{background:this.props.showBtn.id == item.id && this.state.mouse.done ? "#ddd" : "white"}} onMouseLeave={this.moveMouse(false,item.id)} onMouseEnter={this.moveMouse(true,item.id)} key={item.id} className={list.listLi}>
              <Item {...item} deleteTodoList={this.props.deleteTodoList} showBtn={this.state.mouse} ></Item>
            </li>
          })}
        </ul>
      </div>
    )
  }
}
