import React, { Component } from 'react'
import footer from './index.module.css'

export default class Footer extends Component {

  state = {
    all:false
  }

  finish = (e)=>{
    this.setState({all:e.target.checked})
  }

  deleteAll = ()=>{
    if(this.state.all){
      this.props.deleteAll
    }
  }

  componentDidUpdate(){
    console.log(this.state.all)
  }

  render() {
    return (
      <div style={{ marginTop: '20px',display:'flex',justifyContent:'space-between'}}>
        <label className={footer.lab}>
          <input type="checkBox" onChange={this.finish}/>
          <span>已完成/全部({this.props.total})</span>
        </label>
        <button className={footer.btn} onClick={this.deleteAll}>清除已选选项</button>
      </div>
    )
  }
}
